    private Calendar nascita;

    public static Session getInstance() {
        return ourInstance;
    }

    private Session() {
    }

    public static Calendar getDataNascita() {
        return ourInstance.nascita;
    }

    public static void setDataNascita(Calendar value) {
        ourInstance.nascita = value;
    }

    public static int getAge() {
        Calendar today = Calendar.getInstance();
        Calendar birthDate = ourInstance.nascita;

        long m1 = today.getTimeInMillis();
        long m2 = birthDate.getTimeInMillis();

        int days = (int) ((m1 - m2) / (1000 * 60 * 60 * 24));

        return Math.round( Math.round( days / 365.0 ) );
    }

    public static double getTassoTm11(int age, int anni) {
        return ourInstance.tassi_tm11[age][anni];
    }

    public static double getTassoC45(int age, int anni, boolean provv50) {
        if (provv50) return ourInstance.tassi_c45_50[age][anni];
        else return ourInstance.tassi_c45[age][anni];
    }

    static double getTassoEsonero(int age, int anni) {
        int s = age + anni;
        if (s <= 60) return 0.01;
        switch (s) {
            case 61:
                return 0.015;
            case 62:
                return 0.02;
            case 63:
                return 0.025;
            case 64:
                return 0.03;
            default:
                return 0.035;
        }
    }

    static double getCaricamentoPu(double B15, double provv) {

        if (B15 < 20000) return 0.006 + 0.024 * provv;
        if (B15 < 50000)
            return (20000 * (0.006 + 0.024 * provv) + (B15 - 20000) * (0.005 + 0.02 * provv)) / B15;
        if (B15 < 80000)
            return (20000 * (0.006 + 0.024 * provv) + 30000 * (0.005 + 0.02 * provv) + (B15 - 50000) * (0.004 + 0.016 * provv)) / B15;
        else
            return (20000 * (0.006 + 0.024 * provv) + 30000 * (0.005 + 0.02 * provv) + 30000 * (0.004 + 0.016 * provv) + (B15 - 80000) * (0.003 + 0.012 * provv)) / B15;
    }
